	repositories {
		maven {
		    url 'http://repo.putnami.org/'
		}
		mavenLocal()
		mavenCentral()
	}
	dependencies {
		classpath 'de.richsource.gradle.plugins:gwt-gradle-plugin:0.4-PWT_patched'
